from GUI import View, Button, Imageimport osclass ImageTestView(View):	def handle_event(self, event):		if event.kind == 'mouse_up':			if self.position[0] <= event.position[0] <= self.position[0] + 39 * 2 and \				 self.position[1] <= event.position[1] <= self.position[1] + 39 * 2:				self.container.imageClicked(event.position)			def setImage(self, imagePath):		self.image = Image(file = os.path.join("Data", "Pics", "Actors", imagePath + ".png"))			def draw(self, c, r):		main_image_pos = self.position		src_rect = (39, 0, 39*2, 39)		dst_rect = (main_image_pos[0], main_image_pos[1], 								main_image_pos[0] + src_rect[2] - src_rect[0] + 39, 								main_image_pos[1] + src_rect[3] - src_rect[1] + 39)		self.image.draw(c, src_rect, dst_rect)		class ViewWithBackButton(View):	def setupComponents(self, parent):		btn = Button(title = "Back", action = self.backClicked, style = 'default')		btn.position = ((self.width - btn.width) / 2, self.height - btn.height - 10)		self.add(btn)			def refreshComponent(self):		pass			def backClicked(self):		self.container.setView(1)	